import mongoose from "mongoose";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";

const employeeSchema = mongoose.Schema(
  {
    email: {
      type: String,
      required: true,
      unique: true,
    },
    company: {
      type: mongoose.Types.ObjectId,
      ref: "Company",
    },
    targets: [
      {type: mongoose.Types.ObjectId,
      ref: "Target"}
      ],
    phoneNumber: {
      type: String,
      trim: true,
    },
    avatar: {
      type: String,
      default:"data:image/png;base64,"
    },
    password: {
      type: String,
      required: true,
    },
    refreshToken: String,
    name: {
      type: String,
    },
    department: {
      type: String,
    },
    jobtitle: {
      type: String,
    },
    jobcategory: {
      type: String,
    },
    gender: {
      type: String,
    },
    startdate: {
      type: Date,
    },
    totalKpiScore: { type: Number, default: 0 },
    totalKpiWeight: { type: Number, default: 0 },
    notifications:[
      {
        message:String,
        date:String,
        seen:{
          type:Boolean,
          default:false
        }
      },
    ]

  },
  { timestamps: true }
);

employeeSchema.pre("save", async function (next) {
  if (!this.isModified("password")) return next();
  this.password = await bcrypt.hash(this.password, 10);
  next();
});

employeeSchema.methods.isPassowrdCorrect = async function (password) {
  return await bcrypt.compare(password, this.password);
};

employeeSchema.methods.genereteAccessToken = function () {
  return jwt.sign(
    {
      id: this._id,
      email: this.email,
    },
    process.env.ACCESS_TOKEN_SECRET,
    {
      expiresIn: process.env.ACCESS_TOKEN_EXPIRY,
    }
  );
};

employeeSchema.methods.genereteRefreshToken = function () {
  return jwt.sign(
    {
      id: this._id,
    },
    process.env.REFRESH_TOKEN_SECRET,
    {
      expiresIn: process.env.REFRESH_TOKEN_EXPIRY,
    }
  );
};

export const Employee = mongoose.model("Employee", employeeSchema);
